script:
frappe.ui.form.on('Library Member', {
    first_name: function(frm) {
        let full_name = (frm.doc.first_name || '') + ' ' + (frm.doc.last_name || '');
        frm.set_value('full_name', full_name.trim());
    },

    last_name: function(frm) {
        let full_name = (frm.doc.first_name || '') + ' ' + (frm.doc.last_name || '');
        frm.set_value('full_name', full_name.trim());
    }
});

frappe.ui.form.on('Library Transaction', {
    refresh: function(frm) {
        // Only show button after the document is saved
        if (!frm.is_new() && frm.doc.status !== 'Returned') {
            frm.add_custom_button('Return Book', function() {
                frm.set_value('status', 'Returned');
                frm.save();
            });
        }
    }
});
